// import React, {Component} from "react";

import React, { useEffect, useState } from 'react'

function MemeGenerator() {
    const [memeData, setMemeData] = useState([])
    const [text, setText] = useState({
        topText: '',
        bottomText: '',
        randomImg: "https://i.imgflip.com/261o3j.jpg"

    })
    useEffect(()=>{
        fetch("http://127.0.0.1:9292/memes")
            .then(res => res.json())
            .then(data => setMemeData(data))
    },[])

    console.log(memeData)


    function handleTop(event){
        console.log("working!")
        setText({...text, topText: event.target.value})
    }
    function handleBottom(event){
        console.log("working!")
        setText({...text, bottomText: event.target.value})
    }

    function handleSubmit(event){
        event.preventDefault()
        // console.log("working");
        const randNum = Math.floor(Math.random() *  memeData.length)
        const randMemeImg =  memeData[randNum].url
        setText({ randomImg: randMemeImg})
    }

    // // function randNum(){
    //     return(Math.floor(Math.random() * 15) + 1)
    // }

    return (
        <>
            <form className="meme-form" onSubmit={handleSubmit}>
                <input
                    type = "text"
                    name = "topText"
                    placeholder="Top Text"
                    value = {text.topText}
                    onChange = {handleTop}
                />
                <input
                    type = "text"
                    name = "bottomText"
                    placeholder="Bottom Text"
                    value = {text.bottomText}
                    onChange = {handleBottom}
                />
                <button>Generate</button>




            </form>

            <div className='meme' >
                 <img src={memeData.url} alt= "img"/>

                 <h2 className="bottom">{text.tText}</h2> 

                <h2 className="bottom">{text.bottomText}</h2>
            </div>


        </>
    )
}


