// import React, {Component} from "react";

import React, { useEffect, useState } from 'react'

function MemeGenerator() {
    const [memeData, setMemeData] = useState([])
    const [text, setText] = useState({
        topText: '',
        bottomText: ''
    })
    useEffect(()=>{
        fetch("http://127.0.0.1:9292/memes")
            .then(res => res.json())
            .then(data => setMemeData(data))
    },[])

    console.log(memeData)


    function handleTop(event){
        console.log("working!")
        setText({...text, topText: event.target.value})
    }
    function handleBottom(event){
        console.log("working!")
        setText({...text, bottomText: event.target.value})
    }

    function handleSubmit(event){
        event.preventDefault()
        console.log(text)
        // const randNum = Math.floor(Math.random() * this.state.allMemeImgs.length)
        // const randMemeImg = this.state.allMemeImgs[randNum].url
        // this.setState({ randomImg: randMemeImg})
    }

    function randNum(){
        return(Math.floor(Math.random() * 15) + 1)
    }

    return (
        <>
            <div>
            <form className="meme-form" onSubmit={handleSubmit}>
                <input
                    type = "text"
                    name = "topText"
                    placeholder="Top Text"
                    value = ''
                    onChange = {handleTop}
                />
                <input
                    type = "text"
                    name = "bottomText"
                    placeholder="Bottom Text"
                    value = ''
                    onChange = {handleBottom}
                />
                <button>Generate</button>
            </form>
            <div className="meme">
                <img src={memeData[randNum].url} alt=""/>
                <h2 className="top">{text.topText}</h2>
                <h2 className="bottom">{text.bottomText}</h2>
            </div>
        </div>
        </>
    )
}


